} from 'recharts';
import { Link } from 'react-router-dom';
import { Badge } from '@/components/ui/badge';

interface Stats {
  orderCount: number;
  revenue: number;
  productCount: number;
  customerCount: number;
  visits: number;
  saved: number;
}

interface Order {
  id: string;
  orderNumber: string;
  customer: { name: string; email: string };
  total: number;
  status: string;
  createdAt: string;
}

interface Notification {
  id: string;
  type: 'order' | 'stock' | 'payment' | 'review';
  title: string;
  message: string;
  time: string;
  read: boolean;
}

interface SalesData {
  name: string;
  revenue: number;
  orders: number;
}

export default function Dashboard() {
  const [stats, setStats] = useState<Stats | null>(null);
  const [recentOrders, setRecentOrders] = useState<Order[]>([]);
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [salesData, setSalesData] = useState<SalesData[]>([]);
  const [loading, setLoading] = useState(true);
  const [dateRange, setDateRange] = useState('month');
  const [chartView, setChartView] = useState('visual');

  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      
      const [statsData, ordersData] = await Promise.all([
        coreApi.get('/dashboard/stats'),
        coreApi.getOrders()
      ]);

      // Add calculated fields
      setStats({
        ...statsData,
        visits: statsData.visits || 0,
        saved: statsData.saved || 0
      });
      
      const orders = Array.isArray(ordersData) ? ordersData : ((ordersData as any)?.orders || []);
      setRecentOrders(orders.slice(0, 5));

      const chartData = generateChartData(orders);
      setSalesData(chartData);

      const notifs = generateNotifications(orders, statsData);
      setNotifications(notifs);

    } catch (error) {
      console.error('Failed to load dashboard data:', error);
      setStats({ 
        orderCount: 0, 
        revenue: 0, 
        productCount: 0, 
        customerCount: 0,
        visits: 0,
        saved: 0
      });
      setRecentOrders([]);
      setSalesData([]);
      setNotifications([]);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadDashboardData();
  }, [loadDashboardData, dateRange]);

  const generateChartData = (orders: Order[]): SalesData[] => {
    const dataMap = new Map();
    const now = new Date();
    
    for (let i = 6; i >= 0; i--) {
      const date = new Date(now);
