import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { authApi, coreApi } from '@/lib/api';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { Loader2, Package, User, LogOut } from 'lucide-react';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';

export default function Profile() {
  const [user, setUser] = useState<any>(null);
  const [orders, setOrders] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();
  const { toast } = useToast();

  useEffect(() => {
    loadData();
  }, []);

  async function loadData() {
    try {
      const userData = await authApi.me();
      setUser(userData);

      // Only load orders if we have a user
      if (userData) {
        try {
          // Note: This assumes getOrders returns a list of orders for the current user
          // If the API is different, we might need to adjust
          const ordersData = await coreApi.getOrders();
          setOrders(Array.isArray(ordersData) ? ordersData : []);
        } catch (error) {
          console.error('Failed to load orders:', error);
        }
      }
    } catch (error) {
      console.error('Failed to load profile:', error);
      navigate('/auth/login');
    } finally {
      setIsLoading(false);
    }
  }

  function handleLogout() {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    navigate('/auth/login');
    toast({
      title: 'Logged out',
      description: 'You have been successfully logged out.',
    });
  }

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Loader2 className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  if (!user) {
    return null;
  }

  return (
    <div className="container py-10">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">My Account</h1>
        <Button variant="outline" onClick={handleLogout}>
          <LogOut className="mr-2 h-4 w-4" />
          Logout
        </Button>
      </div>

      <Tabs defaultValue="orders" className="w-full">
        <TabsList className="mb-8">
          <TabsTrigger value="orders">
            <Package className="mr-2 h-4 w-4" />
            Orders
          </TabsTrigger>
          <TabsTrigger value="profile">
            <User className="mr-2 h-4 w-4" />
            Profile Details
          </TabsTrigger>
        </TabsList>

        <TabsContent value="orders">
          <Card>
            <CardHeader>
              <CardTitle>Order History</CardTitle>
              <CardDescription>View and track your recent orders.</CardDescription>
            </CardHeader>
            <CardContent>
              {orders.length === 0 ? (
                <div className="text-center py-10 text-muted-foreground">
                  No orders found.
                </div>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Order ID</TableHead>
                      <TableHead>Date</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Total</TableHead>
                      <TableHead>Action</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {orders.map((order) => (
                      <TableRow key={order.id}>
                        <TableCell className="font-medium">#{order.orderNumber || order.id.slice(0, 8)}</TableCell>
                        <TableCell>{new Date(order.createdAt).toLocaleDateString()}</TableCell>
                        <TableCell>
                          <Badge variant={
                            order.status === 'COMPLETED' ? 'default' :
                            order.status === 'PENDING' ? 'secondary' :
                            order.status === 'CANCELLED' ? 'destructive' : 'outline'
                          }>
                            {order.status}
                          </Badge>
                        </TableCell>
                        <TableCell>${Number(order.totalAmount).toFixed(2)}</TableCell>
                        <TableCell>
                          <Button variant="ghost" size="sm" onClick={() => navigate(`/orders/${order.id}`)}>
                            View Details
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="profile">
          <Card>
            <CardHeader>
              <CardTitle>Profile Information</CardTitle>
              <CardDescription>Update your personal details.</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid gap-2">
                <Label htmlFor="email">Email</Label>
                <Input id="email" value={user.email} disabled />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="role">Role</Label>
                <Input id="role" value={user.role} disabled />
              </div>
              {/* Add more fields as needed */}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { coreApi } from '@/lib/api';
import { Loader2 } from 'lucide-react';

export default function StorefrontHome() {
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    redirectToHomePage();
  }, []);

  const redirectToHomePage = async () => {
    try {
      // Try to find a page with slug 'home' or the first page
      const pages = await coreApi.getPages();
      
      if (Array.isArray(pages) && pages.length > 0) {
        // Look for 'home' page first
        const homePage = pages.find((p: { slug: string }) => p.slug === 'home');
        
        if (homePage) {
          navigate(`/${homePage.slug}`, { replace: true });
        } else {
          // Redirect to first page
          navigate(`/${pages[0].slug}`, { replace: true });
        }
      } else {
        setLoading(false);
      }
    } catch (error) {
      console.error('Failed to load pages', error);
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Loader2 className="animate-spin w-12 h-12 text-indigo-600" />
      </div>
    );
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="text-center p-8">
        <h1 className="text-4xl font-bold text-gray-800 dark:text-gray-100 mb-4">
          Welcome to Our Store
        </h1>
        <p className="text-gray-600 dark:text-gray-400 mb-8">
          No pages have been created yet. Please create a page to get started.
        </p>
        <div className="bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg p-4">
          <p className="text-yellow-800 dark:text-yellow-200">
            <strong>Tip:</strong> Create a page with slug "home" to make it your homepage.
          </p>
        </div>
      </div>
    </div>
  );
}

import { apiClient } from './core/api-client';
import type { ShippingZone, CreateShippingZoneData } from './types';

export const shippingService = {
  getShippingZones: (): Promise<ShippingZone[]> =>
    apiClient.fetch(`${apiClient.coreUrl}/shipping/zones`, {
      requireAuth: true,
    }),

  createShippingZone: (data: CreateShippingZoneData): Promise<ShippingZone> =>
    apiClient.fetch(`${apiClient.coreUrl}/shipping/zones`, {
      method: 'POST',
      body: JSON.stringify(data),
      requireAuth: true,
    }),

  updateShippingZone: (id: string, data: Partial<CreateShippingZoneData>): Promise<ShippingZone> =>
    apiClient.fetch(`${apiClient.coreUrl}/shipping/zones/${id}`, {
      method: 'PUT',
      body: JSON.stringify(data),
      requireAuth: true,
    }),

  deleteShippingZone: (id: string): Promise<void> =>
    apiClient.fetch(`${apiClient.coreUrl}/shipping/zones/${id}`, {
      method: 'DELETE',
      requireAuth: true,
    }),

  calculateShipping: (data: any): Promise<any> =>
    apiClient.fetch(`${apiClient.coreUrl}/shipping/calculate`, {
      method: 'POST',
      body: JSON.stringify(data),
    }),
};

import { apiClient } from './core/api-client';

export interface StaffUser {
  id: string;
  email: string;
  createdAt: string;
  updatedAt: string;
  staffPermissions: {
    permission: string;
    grantedAt: string;
    grantedByUser: {
      email: string;
    };
  }[];
}

export interface CreateStaffDto {
  email: string;
  permissions: string[];
}

export const staffService = {
  getStaffUsers: (page: number = 1, limit: number = 50): Promise<{ data: StaffUser[]; meta: any }> =>
    apiClient.fetch(`${apiClient.authUrl}/staff?page=${page}&limit=${limit}`, {
      requireAuth: true,
    }),

  getStaffUser: (id: string): Promise<StaffUser> =>
    apiClient.fetch(`${apiClient.authUrl}/staff/${id}`, {
      requireAuth: true,
    }),

  createStaff: (data: CreateStaffDto): Promise<StaffUser> =>
    apiClient.fetch(`${apiClient.authUrl}/staff`, {
      method: 'POST',
      body: JSON.stringify(data),
      requireAuth: true,
    }),

  updatePermissions: (id: string, permissions: string[]): Promise<{ message: string; permissions: string[] }> =>
    apiClient.fetch(`${apiClient.authUrl}/staff/${id}/permissions`, {
      method: 'PUT',
      body: JSON.stringify({ permissions }),
      requireAuth: true,
    }),

  deleteStaff: (id: string): Promise<void> =>
    apiClient.fetch(`${apiClient.authUrl}/staff/${id}`, {
      method: 'DELETE',
      requireAuth: true,
    }),
};
